    public static ArrayList<SekolahIkatanDinas> getListData(){
        SekolahIkatanDinas sekolahikatandinas = null;
        ArrayList<SekolahIkatanDinas> list = new ArrayList<>();
        for (String[] aData : data) {
            sekolahikatandinas = new SekolahIkatanDinas();
            sekolahikatandinas.setName(aData[0]);
            sekolahikatandinas.setRemarks(aData[1]);
            sekolahikatandinas.setPhoto(aData[2]);
            sekolahikatandinas.setPenjelasan(aData[3]);

            list.add(sekolahikatandinas);
        }

        return list;
    }
